const camposIsolados = [{
  campo: "#TVeicVeiculoVeicTveiCodigo",
  seletorErro: ".alert.alert-danger",
  mensagem: "Informe o tipo",
  tipo: "select",
  valorOriginal: "CAVALO"
}, {
  campo: "#select2-TVeicVeiculoVeicVteiCodigo-container",
  seletorErro: "#erro_eixo",
  mensagem: "Informe o tipo de eixo",
  tipo: "tipoEixo",
  valorOriginal: "2 eixos (1 simples rodagem simples + 1 simples rodagem dupla- 6 pneus)"
}, {
  campo: "#TVeicVeiculoVeicCidaCodigoEmplacamento",
  seletorErro: ".alert.alert-danger",
  mensagem: "Informe a cidade de emplacamento",
  tipo: "select",
  valorOriginal: "BLUMENAU"
}, {
  campo: "#Tracker0TVtecVersaoTecnologiaVtecCodigo",
  seletorErro: ".alert.alert-danger",
  mensagem: "Versão da Tecnologia deve ser informado",
  tipo: "select",
  valorOriginal: "SASCAR - NORMAL"
}, {
  campo: "#TVeicVeiculoVeicDataVencLicenciamento",
  seletorErro: ".alert.alert-danger",
  mensagem: "Vencimento do licenciamento inválido ou vencido",
  tipo: "input",
  valorOriginal: "17/10/2030"
}];
camposIsolados.forEach(({
  campo,
  seletorErro,
  mensagem,
  tipo,
  valorOriginal
}) => {
  if (tipo === "tipoEixo") {
    submeterEValidarErro(seletorErro, mensagem);
    // Preenche novamente o eixo para os próximos testes
    cy.get(campo).click();
    cy.contains(".select2-results__option", valorOriginal, {
      timeout: 10000
    }).click();
    return;
  }
  if (tipo === "select2") {
    cy.get("select[name='data[TVeicVeiculo][veic_vtei_codigo]']").select("");
  } else if (tipo === "select") {
    cy.get(campo).select("");
  } else {
    cy.get(campo).clear();
  }
  submeterEValidarErro(seletorErro, mensagem);
  if (tipo === "select2") {
    cy.get(campo).click();
    cy.contains(".select2-results__option", valorOriginal, {
      timeout: 10000
    }).click();
  } else if (tipo === "select") {
    cy.get(campo).select(valorOriginal);
  } else if (tipo === "input") {
    cy.get(campo).type(valorOriginal);
  }
});

